package gaurav.Assignment18;

public class AsciiValue {

		
		void getCharOfAscii(int Value) {
			
			char ch=(char)Value;
		 
			System.out.println("Char value of "+Value+ " is: " + ch);
			
		}
		
		public static void main(String[] args) {
			// TODO Auto-generated method stub
			AsciiValue asciiVaule = new AsciiValue();
			asciiVaule.getCharOfAscii(65);
				
			
		}

	}
		




