package shashank_array;

public class AsciiSmallCase {

	public void getascii() {
		for (int i=97;i<=122;i++) {
			System.out.println("Character for ASCII value  "+i+" is "+(char)i);
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				AsciiSmallCase asciiSmallCase = new AsciiSmallCase();
				asciiSmallCase.getascii();
	}

}
